[backcolor=rgb(255, 247, 160)][size=12px][size=14px][size=12px][mw_shl_code=java,true]import java.util.*;
 
public class Solution {
    private HashMap<Integer, Integer> pos = new HashMap<>();
 
    public Solution(int[] input) {
 
        HashSet<Integer> set = new HashSet<>();
        for (int num : input) {
            set.add(num);
        }
        List<Integer> list = new ArrayList<>(set);
        Collections.sort(list);
 
        for (int i = 0; i < list.size(); i++) {
            pos.put(list.get(i), i);
        }
    }
 
    public int query(int num) {
        if (!pos.containsKey(num)) {
            return -1;
        }
        return pos.get(num);
    }
