[backcolor=rgb(255, 247, 160)][size=12px][size=14px][size=12px][mw_shl_code=java,true]import java.util.HashSet;
 
class ListNode {
    public int data;
    public ListNode next;
}
 
public class Solution {
    public ListNode helper(ListNode[] arr) {
        if (arr.length == 0) {
            return null;
        }
        HashSet<ListNode> set = new HashSet<>();
        for (int i = 0; i < arr.length; i++) {
            if (set.contains(arr[i])) {
                continue;
            }
            ListNode cur = arr[i].next;
            while (cur != null && !set.contains(cur)) {
                set.add(cur);
                cur = cur.next;
            }
        }
        for (int i = 0; i < arr.length; i++) {
            if (!set.contains(arr[i])) {
                return arr[i];
            }
        }
        return null;
    }
