        int min = Integer.MAX_VALUE;
        ListNode res = null;
        for (int i = 0; i < arr.length; i++) {
            ListNode cur = arr[i];
            int count = 1;
            while (cur != null) {
                count++;
                cur = cur.next;
            }
            if (count < min) {
                min = count;
                res = arr[i];
            }
        }
